var xmlhttp = new XMLHttpRequest();
xmlhttp.open('GET', 'https://my-json-server.typicode.com/IlyaLytvynov/ads-box-server/ads', true);
xmlhttp.onreadystatechange = function() {
    if (xmlhttp.readyState === 4 && xmlhttp.status === 200) {
        var obj = JSON.parse(xmlhttp.responseText);

        var div;
        var title;
        var img;
        var description;
        var note;
        var url;
        var i;
        var content = document.querySelector('.content');
        var item;

        for (i = 0; i < obj.length; i++) {
            item = document.createElement('div');
            item.className = 'item';
            title = document.createElement('div');
            title.className = 'item-header';
            div.textContent = obj[i].title;
            item.appendChild(div);
            div.className = 'item-description';
            div.textContent = obj[i].description;
            item.appendChild(div);
            content.appendChild(item);
        }
    }

    //document.querySelector('.content').innerHTML = name;
};
xmlhttp.send(null);
